Rule.Source:

import sailpoint.object.Identity;
import org.apache.commons.logging.Log;
import org.apache.commons.logging.LogFactory; 
import sailpoint.object.Filter;
import sailpoint.object.Filter.FilterCompiler;
import java.util.Map;
import sailpoint.object.QueryOptions;
import sailpoint.object.Filter;
import org.apache.log4j.Logger;
import org.apache.log4j.Level;

Logger log = Logger.getLogger("Rule.WebService.Correlation");
// log.setLevel((Level) Level.ERROR);

log.debug("Enter rule  CorrelationRule");

Map correlationMap = new HashMap();

List emaillist = account.getAttribute("emails.primary.value");
log.debug("emaillist ***" + emaillist);

if(null != emaillist @and emaillist.size()>0){

	for(String str: emaillist){
		log.debug("str***" + str);


		Identity identity = context.getUniqueObject(Identity.class, Filter.ignoreCase(Filter.eq("email", str)));
		if(identity != null){
			log.debug("identity not null***" + identity);
			correlationMap.put("identity", identity);
			break;
		}
	}
}


// return map
return correlationMap;
